document.addEventListener("DOMContentLoaded", ( () => {
    const forms = document.querySelectorAll("form");
    if (forms.length > 0) {
        forms.forEach((form => {
            form.addEventListener("submit", (function(e) {
                e.preventDefault();
                window.throttleSubmitForm(this)
            }
            ))
        }
        ))
    }
    const modelsCreditLinks = document.querySelectorAll(".js-credit-goal");
    if (modelsCreditLinks.length > 0) {
        modelsCreditLinks.forEach((el => {
            el.addEventListener("click", ( () => window.yaReachGoal("credit")))
        }
        ))
    }
    document.querySelectorAll(".header-bottom__item").forEach((item => {
        item.addEventListener("click", ( () => {
            document.getElementById(item.getAttribute("href").slice(1))
        }
        ))
    }
    ));
    window.cookieAgreement()
}
));
