document.addEventListener("DOMContentLoaded",(()=>{if(document.querySelector("[data-models-scroll-up]")){const scrollBtn=document.querySelector("[data-models-scroll-up]");const modelsContainer=document.querySelector("#models");let btnShown=false;window.addEventListener("scroll",(()=>modelsScrollUpToggle()));scrollBtn.addEventListener("click",(()=>{modelsContainer.scrollIntoView({behavior:"smooth",block:"start"})}));function modelsScrollUpToggle(){const modelsContainerOffsetTop=modelsContainer.getBoundingClientRect().top+window.pageYOffset||document.documentElement.scrollTop,modelsContainerHeight=modelsContainer.getBoundingClientRect().height,modelsContainerOffsetBottom=modelsContainerOffsetTop+modelsContainerHeight,windowScrollBottom=window.pageYOffset+window.innerHeight;if(window.pageYOffset>=modelsContainerOffsetTop+150&&windowScrollBottom<=modelsContainerOffsetBottom){btnShown?"":fadeIn(scrollBtn,100)}else{btnShown?fadeOut(scrollBtn,100):""}}const fadeIn=(el,timeout,display)=>{el.style.opacity=0;el.style.display=display||"block";el.style.transition=`opacity ${timeout}ms`;setTimeout((()=>{el.style.opacity=1}),10);btnShown=true};const fadeOut=(el,timeout)=>{el.style.opacity=1;el.style.transition=`opacity ${timeout}ms`;el.style.opacity=0;setTimeout((()=>{el.style.display="none"}),timeout);btnShown=false}}}));